import React, {useState} from "react";

const App =() =>{

  const [inputData, setInputData] = useState();
  const [items, setItems] = useState([]);

  const addItem =(e) =>{
    if(e.keyCode === 13){
      if(!inputData){
        alert("input field is empty");
      }
      else{
    setItems([...items,inputData ]);
    setInputData("");
      }

    }
  }
  const deleteItem =(id) =>{
   const updateditem= items.filter((elem, ind)=>{
      return ind !== id;
    });
    setItems(updateditem);
  }




  return (
  <>
<div className="main_div">
  <div className="center_div">
        <br />
        <h1> ToDo List </h1>
        <br />
        <input 
        type="text" 
        placeholder="Add a Items"
        value={inputData}
        onChange={(e)=>{setInputData(e.target.value)}} onKeyUp={addItem} />

      <ol>
        {items.map((elem, ind)=>{
          return (
          <div className="todo_style" key={ind}>
      <i class="fa fa-times" aria-hidden="true"onClick={()=>{deleteItem(ind)}}></i>
      <i class="fa fa-edit" aria-hidden="true"></i>
          <li>{elem}</li>
          </div>
          )
        })}
      </ol>



</div>
</div>

  </>

    );
}

export default App;
